    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        
         if("".equals(jTextField3.getText()))
        {
            JOptionPane.showMessageDialog(this,"ENTER PARTY NUMBER");
        }
         else if("SELECT".equals(jComboBox1.getSelectedItem()))
        {
            JOptionPane.showMessageDialog(this,"SLECT TYPE");
        }
         else if("".equals(jTextField1.getText()))
        {
            JOptionPane.showMessageDialog(this,"ENTER PARTY NAME");
        }
        else  if("".equals(jTextField2.getText()))
        {
            JOptionPane.showMessageDialog(this,"ENTER CANDIDATE NAME");
        }
         else
        {
          Connection con;
        Statement st,st1;
        ResultSet rs;
        try {
            Class.forName("com.mysql.jdbc.Driver");
            con = (Connection) DriverManager.getConnection("jdbc:mysql://localhost:3306/evom", "root", "a");
            st = (Statement) con.createStatement();
             st1 = (Statement) con.createStatement();
           String Query = " insert into party(type,partyname,cname,partyno)values('" + jComboBox1.getSelectedItem()+ "','" + jTextField1.getText() + "','" + jTextField2.getText() + "','" + jTextField3.getText() + "') ";
            st.executeUpdate(Query);
             String Query1 = " insert into totalvotes(party,partyno) values('"+jTextField1.getText()+"','"+jTextField3.getText()+"')";
             st1.executeUpdate(Query1);
            JOptionPane.showMessageDialog(this, "DATA INSERTED SUCCESSFULLY");
             jComboBox1.setSelectedItem("SELECT");
            jTextField1.setText("");
            jTextField2.setText("");
            jTextField3.setText("");
          

        } catch (ClassNotFoundException | SQLException e) {
            System.out.println(e);
        }
        }
        
        
        // TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

