    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed

           com.mysql.jdbc.Connection con;
        com.mysql.jdbc.Statement st;
        com.mysql.jdbc.ResultSet rs;
        try {
            Class.forName("com.mysql.jdbc.Driver");
            con = (com.mysql.jdbc.Connection) DriverManager.getConnection("jdbc:mysql://localhost:3306/evom", "root", "a");
            st = (com.mysql.jdbc.Statement) con.createStatement();
            String Query = "select * from party where partyno like '" + jTextField3.getText() + "'";
            rs = (com.mysql.jdbc.ResultSet) st.executeQuery(Query);
            while (rs.next()) {
                jComboBox1.setSelectedItem(rs.getString(4));
              jTextField1.setText(rs.getString(2));
              jTextField2.setText(rs.getString(3));
              jTextField3.setText(rs.getString(1));
           }
        } catch (Exception e) {
            System.out.println(e);
        }

        
        
        // TODO add your handling code here:
    }//GEN-LAST:event_jButton3ActionPerformed

